package ljj.sta;

public class ForTest2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Double d1=100.0;
		Double d2=100.0;
		Double d3=200.0;
		Double d4=200.0;
		Integer i1=100;
		Integer i2=100;
		Integer i3=200;
		Integer i4=200;
		System.out.println(Double.valueOf(d1));
		System.out.println(d2);
		System.out.println(d3);
		System.out.println(d4);
		System.out.println(Double.valueOf(d1)==Double.valueOf(d2));
		System.out.println(d3==d4);
		System.out.println(i1);
		System.out.println(i2);
		System.out.println(i3);
		System.out.println(i4);
		System.out.println(Integer.valueOf(i1));
		System.out.println(Integer.valueOf(i2));
		System.out.println(Integer.valueOf(i3));
		System.out.println(Integer.valueOf(i4));
		System.out.println(i1==i2);
		System.out.println(i3==i4);
	
	}

}
